import React from "react";
// import io from "socket.io-client";
import "./talk.css";

const Socket = require("socket.io-client")("http://localhost:3001");

class Talk extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: "",
      context: [],
    };
  }

  ioCommit = () => {
    let ioMsg = "";
    Socket.emit("chat message", this.state.inputValue);
    Socket.on("chat message", function (msg) {
      ioMsg = msg;
    });
    this.setState({
      inputValue: "",
      context: [...this.state.context, ioMsg],
    });
    return false;
  };

  handleChange = (event) => {
    this.setState({ inputValue: event.target.value });
  };

  render() {
    return (
      <div>
        <ul id="messages">{this.state.context}</ul>
        <form action="">
          <input
            autoComplete="off"
            id="m"
            value={this.state.inputValue}
            onChange={this.handleChange}
          />
          <button onClick={this.ioCommit}>Send</button>
        </form>
      </div>
    );
  }
}

export default Talk;
