import React from "react";
import io from "socket.io-client";
import openSocket from "socket.io-client";
import "./talk.css";

const socket = io();

class Talk extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: "",
      context: [],
    };
    this.io();
  }

  ioCommit = () => {
    let inputValue = this.state.inputValue;
    let ioMsg = "";
    console.log(inputValue);
    Socket.emit("chat message", inputValue);
    Socket.on("chat message", function (msg) {
      ioMsg = msg;
    });
    this.setState({
      inputValue: "",
      context: [...this.state.context, ioMsg],
    });
    return false;
  };

  render() {
    return (
      <div>
        <ul id="messages">{this.state.context}</ul>
        <form>
          <input
            id="m"
            value={this.state.inputValue}
            onChange={this.ioCommit}
          />
          <button>Send</button>
        </form>
      </div>
    );
  }
}

export default Talk;
